<span class="line"><span style="color:#9CDCFE;">      gl_Position</span><span style="color:#D4D4D4;"> =</span><span style="color:#DCDCAA;">vec4</span><span style="color:#D4D4D4;">(</span><span style="color:#B5CEA8;">0.0</span><span style="color:#D4D4D4;">,</span><span style="color:#B5CEA8;">0.0</span><span style="color:#D4D4D4;">,</span><span style="color:#B5CEA8;">0.0</span><span style="color:#D4D4D4;">,</span><span style="color:#B5CEA8;">1.0</span><span style="color:#D4D4D4;">);</span></span>
